# @orm.reconstructor这个装饰器
@orm.reconstructor
def __init__(self):
self.fields = ['id', 'email', 'nickname']

def keys(self):
return self.fields

# 支持隐藏字段
def hide(self, *keys):
[self.fields.remove(key) for key in keys]
# 支持添加字段
